import { FETCH_TODOS_1, FETCH_TODOS_1_FAILED, FETCH_TODOS_1_SUCCESS } from "../action_types";

const initialState = {
   isLoading: null,
   todos: [],
};

const todos_1_reducer = (state = initialState, action) => {
   switch (action.type) {
      case FETCH_TODOS_1:
         return { ...state, isLoading: true };
      case FETCH_TODOS_1_SUCCESS:
         return { ...state, loading: false, todos: action.payload };
      case FETCH_TODOS_1_FAILED:
         return { ...state, todos: null };
      default:
         return state;
   }
};

export default todos_1_reducer;
