import React, { useState } from "react";
import ChangingTitleRenderer from "./TodoItemTitle/ChangingTitleRenderer";
import DefaultTitleRenderer from "./TodoItemTitle/DefaultTitleRenderer";

const TodoItemContainer = ({
   id,
   title,
   completed,
   completeTaskClickHandler,
   changeTaskClickHandler,
   removeTaskClickHandler,
}) => {
   const [isChanging, setIsChanging] = useState(false);
   return (
      <div>
         {!isChanging ? (
            <DefaultTitleRenderer title={title} />
         ) : (
            <ChangingTitleRenderer
               title={title}
               id={id}
               changeTaskClickHandler={changeTaskClickHandler}
               setIsChanging={setIsChanging}
            />
         )}
         {completed === false && (
            <button onClick={() => completeTaskClickHandler(id)} disabled={isChanging ? true : false}>
               done
            </button>
         )}
         {completed === false && <button onClick={() => setIsChanging(true)}>change</button>}
         <button onClick={() => removeTaskClickHandler(id)}>remove</button>
      </div>
   );
};

export default TodoItemContainer;
