import React, {useState} from "react";

const ChangingTitleRenderer = ({ title, changeTaskClickHandler }) => {
   const [newTitle, setNewTitle] = useState({ title });
   const changeTitleHandler = (value) => {
      setNewTitle(value);
   };
   return (
      <div>
         <input type="text" onChange={(event) => changeTitleHandler(event.target.value)} value={newTitle} />
         <button onClick={() => changeTaskClickHandler(newTitle)}>Confirm new title</button>
      </div>
   );
};
export default ChangingTitleRenderer;
