import { FETCH_TODOS_1 } from "../action_types";

const initialState = {
   loading: true,
   todos_1: [],
};
const TODOS_1_URL = "https://jsonplaceholder.typicode.com/todos";

const fetchTodos_1 = async (state) => {
   try {
      if (state.todos_1.length) return;
      const fetchingData = await fetch(TODOS_1_URL);
      const response = await fetchingData.json();
      return response;
   } catch (err) {
      return console.log(err);
   }
};

const todos_1_reducer = (state = initialState, action) => {
   switch (action.type) {
      case FETCH_TODOS_1:
         return { ...state, loading: false, todos_1: fetchTodos_1(state) };
      default:
         return state;
   }
};

export default todos_1_reducer;
