import { FETCH_TODOS_1, FETCH_TODOS_1_FAILED, FETCH_TODOS_1_SUCCESS } from "../action_types";

const initialState = {
   isLoading: null,
   tasks: [],
   todoTasks: [],
   completeTasks: [],
};

const filterTodoTask = (tasks) => tasks.filter((task) => !task.completed);
const filterCompleteTask = (tasks) => tasks.filter((task) => task.completed);

const todos_1_reducer = (state = initialState, action) => {
   switch (action.type) {
      case FETCH_TODOS_1:
         return { ...state, isLoading: true };
      case FETCH_TODOS_1_SUCCESS:
         return {
            ...state,
            isLoading: false,
            tasks: action.payload,
            todoTasks: filterTodoTask(action.payload),
            completeTasks: filterCompleteTask(action.payload),
         };
      case FETCH_TODOS_1_FAILED:
         return { ...state, isLoading: false, tasks: null };
      default:
         return state;
   }
};

export default todos_1_reducer;
