import React from "react";
import "./TodosList.scss";
import ListTitle from "../ListTitle";
import TodoItemContainer from "./TodoItemContainer";

const TODO_TITLE = "Should to do tasks";
const COMPLETED_TITLE = "Completed tasks";
const classes = "task-item";

const hrLine = <hr style={{ width: "100%", border: "1px solid #fff" }} />;

const TodosList = ({ todos, completeTaskClickHandler, changeTaskClickHandler, removeTaskClickHandler }) => {
   const notCompletedTodos = todos
      .filter((todo) => !todo.completed)
      .map((todoItem) => (
         <>
            <TodoItemContainer
               className={`${classes}__todo`}
               key={todoItem.id}
               id={todoItem.id}
               title={todoItem.title}
               completed={todoItem.completed}
               completeTaskClickHandler={completeTaskClickHandler}
               changeTaskClickHandler={changeTaskClickHandler}
               removeTaskClickHandler={removeTaskClickHandler}
            />
            {hrLine}
         </>
      ));
   const completedTodos = todos
      .filter((todo) => todo.completed)
      .map((todoItem) => (
         <>
            <TodoItemContainer
               className={`${classes}__complete`}
               key={todoItem.id}
               id={todoItem.id}
               title={todoItem.title}
               removeTaskClickHandler={removeTaskClickHandler}
            />
            {hrLine}
         </>
      ));
   return (
      <>
         <div className={`${classes}__wrapper`}>
            <ListTitle classes={classes} title={TODO_TITLE} />
            {notCompletedTodos}
         </div>
         <div className={`${classes}__wrapper`}>
            <ListTitle classes={classes} title={COMPLETED_TITLE} />
            {completedTodos}
         </div>
      </>
   );
};

export default TodosList;
