import React, { useState } from "react";
import Title from "../ListTitle";

const TITLE_TASK_PLACEHOLDER = "Please, add title of todo";
const CREATE_TASK_TITLE = "Please, create the new task";

const CreateTodo = ({ createTaskClickHandler }) => {
   const [title, setTitle] = useState("");
   const changeHandler = (value) => {
      setTitle(value);
   };

   return (
      <div>
         <Title title={CREATE_TASK_TITLE} />
         <input
            type="text"
            onChange={(event) => changeHandler(event.target.value)}
            placeholder={TITLE_TASK_PLACEHOLDER}
         />
         <button
            onClick={() => createTaskClickHandler(Date.now(), title, false)}
            disabled={!title.length ? true : false}
         >
            add
         </button>
      </div>
   );
};

export default CreateTodo;
