import { FETCH_TODOS_1, FETCH_TODOS_1_FAILED, FETCH_TODOS_1_SUCCESS, ADD_NEW_TASK } from "../action_types";

const initialState = {
   isLoading: null,
   tasks: [],
   todoTasks: [],
   completedTasks: [],
};

const filterTodoTask = (tasks) => tasks.filter((task) => !task.completed);
const filterCompletedTask = (tasks) => tasks.filter((task) => task.completed);

const todos_1_reducer = (state = initialState, action) => {
   let newTasks, newTodoTasks, newCompletedTasks;
   switch (action.type) {
      case FETCH_TODOS_1:
         return { ...state, isLoading: true };
      case FETCH_TODOS_1_SUCCESS:
         newTasks = action.payload;
         newTodoTasks = filterTodoTask(newTasks);
         newCompletedTasks = filterCompletedTask(newTasks);
         return {
            ...state,
            isLoading: false,
            tasks: newTasks,
            todoTasks: newTodoTasks,
            completedTasks: newCompletedTasks,
         };
      case FETCH_TODOS_1_FAILED:
         return { ...state, isLoading: false, tasks: null };
      case ADD_NEW_TASK:
         newTasks = [...state.tasks];
         newTasks.push(action.payload);
         newTodoTasks = filterTodoTask(newTasks);
         return {
            ...state,
            tasks: newTasks,
            todoTasks: newTodoTasks,
         };
      case COMPLETE_TASK:
         newTasks = [...state.tasks];
         let newTodos = todos.filter((todo) => todo.id !== id);
         let completedTodo = todos.filter((todo) => todo.id === id)[0];
         completedTodo.completed = true;
         newTodos.push(completedTodo);
      default:
         return state;
   }
};

export default todos_1_reducer;
