import React, { useState } from "react";

const ChangingTitleRenderer = ({ id, title, changeTaskClickHandler, setIsChanging }) => {
   const [newTitle, setNewTitle] = useState(title);
   const changeTitleHandler = (value) => {
      setNewTitle(value);
   };
   const confirmNewTitle = (id, newTitle) => {
      if (!newTitle.trim().length) return;
      setIsChanging(false);
      changeTaskClickHandler(id, newTitle);
   };
   return (
      <div>
         <input type="text" onChange={(event) => changeTitleHandler(event.target.value)} value={newTitle} />
         <button
            onClick={() => confirmNewTitle(id, newTitle)}
            disabled={!newTitle.trim().length || newTitle.trim() === title.trim() ? true : false}
         >
            Confirm new title
         </button>
      </div>
   );
};
export default ChangingTitleRenderer;
