import { useEffect, useState } from "react";
import "./App.css";
import CreateTodo from "./components/CreateTodo";
import TodosList from "./components/TodosList";

function App() {
   const [todos, setTodos] = useState(null);

   const createTodoClickHandler = (id, title, completed) => {
      setTodos([...todos, { id, title, completed }]);
   };
   const completeClickHandler = (id) => {
      let copyTodos = [...todos];
      const getSettedTask = copyTodos.filter((todo) => todo.id === id)[0];
      getSettedTask.completed = true;
      setTodos(copyTodos);
   };

   useEffect(() => {
      fetch("https://jsonplaceholder.typicode.com/todos")
         .then((response) => response.json())
         .then((jsonData) => setTodos(jsonData));
   }, []);

   useEffect(() => {
      console.log(todos);
   }, [todos]);

   if (!todos) return <div>Loading...</div>;
   return (
      <div className="App">
         <CreateTodo createTodoClickHandler={createTodoClickHandler} />
         <TodosList todos={todos} completeClickHandler={completeClickHandler} />
      </div>
   );
}

export default App;
